context.code:
@include Transition($TDuration:$HTDuration);

&:hover {

    opacity: $HOpacity;

    @if $HShadow == shadow {

        @include Themer(box-shadow, #000000, '0px 0px 20px');

    }

    text-decoration: $HDecoration;

    @include Themer(color, $HColor);

}

example.code:
@include SpecialHover(.7s, .5, shadow, underline, Color-3);

.item {

    transition: opacity .7s;

    &:hover {

        opacity: .5;
        box-shadow: 0px 0px 20px #000000;
        text-decoration: underline;
        color: #E3E3E3;

    }
}
